import React from 'react';
import currencies from "./meta/currencies";
import Option from "./Option";


class App extends React.Component {
    constructor() {
        super();
        this.state ={options: currencies}

    }

    render() {
        const displayOptions = this.state.options.map(item => item.display ? null : <Option item={item}/> )
        return(
        <select>
            {displayOptions}
        </select>
        );
    }

}

export default App;
