import React from "react";



function CurrencyBox(props) {
    let test2;
    const locale = props.item.locale;
    const rate = props.state[props.item.code].rate_float;
    // if (props.amount.code === props.item.code) {
    //     test2 = props.amount.rate_float;
    // }
    // const test4 = test3.map(item => item)
// console.log(test3);
    // console.log(test4.code);
    // console.log(test3)
    const converted = props.state.amount * rate;

    return (
        <div>
        <input placeholder={props.item.code} value={converted.toLocaleString(locale, { style: 'currency', currency: props.item.code })} readOnly/>
        <div className="x-sign" onClick={() => {props.handleClick(props.item.id)}}>X</div>
        </div>
    );

}

export default CurrencyBox
