import React from 'react';
import currencies from "./meta/currencies";
import Option from "./Option";
import CurrencyBox from "./CurrencyBox";


class App extends React.Component {
    constructor() {
        super();
        this.state = {
            options: currencies,
            amount: "",
            USD: {},
            EUR: {},
            GBP: {}
        }
        ;
        this.handleClick = this.handleClick.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        this.fetchData()
        setInterval(() => this.fetchData(), 3600000)
    }

    fetchData = () => {
        fetch("https://api.coindesk.com/v1/bpi/currentprice.json")
            .then(response => response.json())
            .then(data => {
                this.setState({
                    USD: data.bpi.USD,
                    EUR: data.bpi.EUR,
                    GBP: data.bpi.GBP
                });
            });
    }

    handleChange(event) {
        this.setState({amount: event.target.value});
    }

    handleClick(id) {
        this.setState(prevState => {
            const updatedView = prevState.options.map(item => {
                if (item.id === id) {
                    item.display = !item.display;
                }
                return item;
            })
            return {options: updatedView};
        });
    }

    render() {
        const displayOptions = this.state.options
            .map(item => item.display ? null :
                <Option
                    key={item.id}
                    item={item}
                    handleClick={this.handleClick}
                />
            );
        const displayBoxes = this.state.options.map(item => item.display ?
            <CurrencyBox
                key={item.id}
                item={item}
                amount={this.state.amount}
                state={this.state}
                handleClick={this.handleClick}
            /> : null);
        return (
            <div className="converter-form">
                <select>{displayOptions}</select>
                <div className="input-bct">
                    <input type="number" onChange={this.handleChange}/>
                    <label>BCT</label>
                </div>
                {displayBoxes}
            </div>
        );
    }

}

export default App;
