import React from "react";



function CurrencyBox(props) {
    let test2;
    const locale = props.item.locale;
    // if (props.amount.bpi.USD.code === props.item.code) {
    //     test2 = props.amount.bpi.USD.rate_loat;
    // }

    const converted = props.amount.amount * test2;
    console.log(props.amount.bpi.USD.code)
    console.log(props.amount.bpi.USD)
    return (
        <div>
        <input placeholder={props.item.code} value={converted.toLocaleString(locale, { style: 'currency', currency: props.item.code })} readOnly/>
        <div className="x-sign" onClick={() => {props.handleClick(props.item.id)}}>X</div>
        </div>
    );

}

export default CurrencyBox
