import React from 'react';
import currencies from "./meta/currencies";
import Option from "./Option";
import CurrencyBox from "./CurrencyBox";


class App extends React.Component {
    constructor() {
        super();
        this.state ={options: currencies}

    }

    render() {
        const displayOptions = this.state.options.map(item => item.display ? null : <Option key={item.id} item={item}/> );
        const displayBoxes = this.state.options.map(item => item.display ? <CurrencyBox key={item.id} item={item} /> : null);
        return(
            <div>
        <select>
            {displayOptions}
        </select>
                {displayBoxes}
            </div>
        );
    }

}

export default App;
