import React from "react";

function CurrencyBox(props) {
    const locale = props.item.locale;
    return (
        <div>
        <input placeholder={props.item.code} value={(props.amount * 5).toLocaleString(locale, { style: 'currency', currency: props.item.code })} readOnly/>
        <div className="x-sign" onClick={() => {props.handleClick(props.item.id)}}>X</div>
        </div>
    );

}

export default CurrencyBox
