import React from 'react';
import currencies from "./meta/currencies";
import Option from "./Option";
import CurrencyBox from "./CurrencyBox";


class App extends React.Component {
    constructor() {
        super();
        this.state = {
            options: currencies,
            amount: ""
        };
        this.handleClick = this.handleClick.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        fetch("https://api.coindesk.com/v1/bpi/currentprice.json").then(response => console.log(response.json()));
    }

    handleChange(event) {
        this.setState({amount: event.target.value});
    }

    handleClick(id) {
        console.log(this.state.options);
        this.setState(prevState => {
            const updatedView = prevState.options.map(item => {
                if (item.id === id) {
                 item.display = !item.display;
                }
                return item
            })
            return {options: updatedView};
        });
        console.log(this.state.options);

    }

    render() {
        const displayOptions = this.state.options
            .map(item => item.display ? null :
                <Option
                    key={item.id}
                    item={item}
                    handleClick={this.handleClick}
                />
            );
        const displayBoxes = this.state.options.map(item => item.display ?
            <CurrencyBox
                key={item.id}
                item={item}
                amount={this.state.amount}
                handleClick={this.handleClick}
            /> : null);
        return (
            <div>

                {displayOptions}
                <input type="number" onChange={this.handleChange}/>
                {displayBoxes}
            </div>
        );
    }

}

export default App;
